// src/components/WordCard.js
import React from 'react';

const WordCard = ({ word, onEdit, onDelete }) => {
  return (
    <div className="word-card">
      <h3>{word.word}</h3>
      <p><strong>Definition:</strong> {word.definition}</p>
      <p><strong>Example:</strong> {word.exampleSentence}</p>
      <button onClick={() => onEdit(word)}>Edit</button>
      <button onClick={() => onDelete(word._id)}>Delete</button>
    </div>
  );
};

export default WordCard;
