import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../styles/EditProfile.css';

const EditProfile = () => {
  const [currentPassword, setCurrentPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmNewPassword, setConfirmNewPassword] = useState('');
  const navigate = useNavigate();

  const goBackHome = () => {
    navigate('/'); // Adjust the route to match your home page path
  };

  const goBackToVocabulary = () => {
    navigate('/vocabulary'); // Adjust the route to match your vocabulary page path
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (newPassword !== confirmNewPassword) {
      alert('New passwords do not match.');
      return;
    }

    try {
      const userId = localStorage.getItem('userId');
      await axios.put(`http://localhost:3000/api/users/${userId}`, {
        currentPassword,
        newPassword,
      });
      alert('Password updated successfully.');
    } catch (error) {
      console.error('Error updating password:', error);
      alert('Failed to update password.');
    }
  };

  return (
    <div className="edit-profile-container">
      <h2>Edit Profile</h2>

      <form onSubmit={handleSubmit}>
        <div>
          <label>Current Password</label>
          <input
            type="password"
            value={currentPassword}
            onChange={(e) => setCurrentPassword(e.target.value)}
            required
          />
        </div>
        <div>
          <label>New Password</label>
          <input
            type="password"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Confirm New Password</label>
          <input
            type="password"
            value={confirmNewPassword}
            onChange={(e) => setConfirmNewPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Update Password</button>
      </form>

      <div className="button-container">
        <button className="return-home" onClick={goBackHome}>
          <i className="fas fa-home"></i> Return Home
        </button>
        <button className="return-vocabulary" onClick={goBackToVocabulary}>
          <i className="fas fa-book"></i> Return to My Vocabulary
        </button>
      </div>
    </div>
  );
};

export default EditProfile;
