import React, { useEffect, useState } from 'react';
import axios from 'axios';

const UpdateWordForm = ({ currentWord, onUpdate, onCancel }) => {
  const [definition, setDefinition] = useState('');
  const [exampleSentence, setExampleSentence] = useState('');

  useEffect(() => {
    // Only initialize form fields if currentWord is defined
    if (currentWord) {
      setDefinition(currentWord.definition);
      setExampleSentence(currentWord.exampleSentence);
    }
  }, [currentWord]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (currentWord) {
      onUpdate(currentWord._id, { definition, exampleSentence });
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Definition:</label>
        <input
          type="text"
          value={definition}
          onChange={(e) => setDefinition(e.target.value)}
          required
        />
      </div>
      <div>
        <label>Example Sentence:</label>
        <input
          type="text"
          value={exampleSentence}
          onChange={(e) => setExampleSentence(e.target.value)}
          required
        />
      </div>
      <button type="submit">Update Word</button>
      <button type="button" onClick={onCancel}>Cancel</button>
    </form>
  );
};

export default UpdateWordForm;
