import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../styles/Vocabulary.css';
import { generateDefinition } from '../services/generateDefinition';
import { deleteWord } from '../services/vocabService';

const API_URL = 'http://localhost:3000/api/vocab';

const Vocabulary = () => {
  const [vocabularyList, setVocabularyList] = useState([]);
  const [userId, setUserId] = useState(null);
  const [showAddForm, setShowAddForm] = useState(false);
  const [newWord, setNewWord] = useState('');
  const [definition, setDefinition] = useState('');
  const [exampleSentence, setExampleSentence] = useState('');
  const [suggestedWords, setSuggestedWords] = useState([]);
  const [userLevel, setUserLevel] = useState('beginner'); // Example user level
  const navigate = useNavigate();
  
  // Fetch suggested words from Datamuse API
  const fetchWordSuggestions = async (word) => {
    try {
      const response = await axios.get(`https://api.datamuse.com/sug?s=${word}`);
      setSuggestedWords(response.data.map(item => ({ word: item.word }))); // Set suggested words
    } catch (error) {
      console.error('Error fetching word suggestions:', error);
    }
  };

  // Fetch vocabulary words for the user
  useEffect(() => {
    const fetchVocabulary = async () => {
      try {
        const userIdFromStorage = localStorage.getItem('userId');
        if (userIdFromStorage) {
          setUserId(userIdFromStorage);
          const response = await axios.get(`${API_URL}?userId=${userIdFromStorage}`);
          setVocabularyList(response.data);
        }
      } catch (error) {
        console.error('Error fetching vocabulary:', error);
      }
    };
    fetchVocabulary();
  }, []);

  // Suggest words when newWord changes
  useEffect(() => {
    if (newWord) {
      fetchWordSuggestions(newWord);
    } else {
      setSuggestedWords([]); // Clear suggestions if no input
    }
  }, [newWord]);

  const handleLogout = () => {
    localStorage.removeItem('userId');
    navigate('/');
  };

  const handleAddClick = () => {
    setShowAddForm(true);
  };

  const handleGenerateDefinition = async () => {
    try {
      const definitionData = await generateDefinition(newWord);
      setDefinition(definitionData.definition);
    } catch (error) {
      console.error('Error generating definition:', error);
    }
  };

  const handleAddWord = async () => {
    try {
      const wordData = { word: newWord, definition, exampleSentence, userId };
      const response = await axios.post(`${API_URL}`, wordData);
      setVocabularyList([...vocabularyList, response.data]); // Update vocabulary list
      handleCancel(); // Reset and close form after saving
    } catch (error) {
      console.error('Error adding word:', error);
    }
  };

  const handleCancel = () => {
    setShowAddForm(false);
    setNewWord('');
    setDefinition('');
    setExampleSentence('');
    setSuggestedWords([]); // Clear suggestions
  };

  const handleDeleteWord = async (id) => {
    try {
      await deleteWord(id);
      setVocabularyList(vocabularyList.filter((vocab) => vocab._id !== id)); // Remove deleted word
    } catch (error) {
      console.error("Error deleting word:", error);
    }
  };

  const handleUpdateWord = (id) => {
    navigate(`/update-word/${id}`); // Navigate to update page
  };

  // Function to handle review score updates
  const handleScoreChange = async (id, newScore) => {
    try {
      const response = await axios.put(`${API_URL}/${id}`, { reviewScore: newScore });
      setVocabularyList(vocabularyList.map((vocab) =>
        vocab._id === id ? { ...vocab, reviewScore: response.data.reviewScore } : vocab
      ));
    } catch (error) {
      console.error("Error updating score:", error);
    }
  };

  return (
    <div className="vocabulary-container">
      <div className="sidebar">
        <h3>Menu</h3>
        <button onClick={handleAddClick}>Add Word</button>
        <button onClick={handleLogout}>Logout</button>
      </div>

      <div className="vocabulary-content">
        <h2>Your Vocabulary</h2>

        {showAddForm && (
          <div className="add-word-form">
            <input
              type="text"
              placeholder="Word"
              value={newWord}
              onChange={(e) => setNewWord(e.target.value)}
              required
            />
            <button onClick={handleGenerateDefinition}>Generate Definition</button>
            <input
              type="text"
              placeholder="Definition"
              value={definition}
              onChange={(e) => setDefinition(e.target.value)}
              required
            />
            <input
              type="text"
              placeholder="Example Sentence"
              value={exampleSentence}
              onChange={(e) => setExampleSentence(e.target.value)}
            />
            <button onClick={handleAddWord}>Save Word</button>
            <button onClick={handleCancel}>Cancel</button>
          </div>
        )}

        <div className="vocabulary-cards">
          {vocabularyList.map((vocab) => (
            <div key={vocab._id} className="vocabulary-card">
              <h3>{vocab.word}</h3>
              <p><strong>Definition:</strong> {vocab.definition}</p>
              <p><strong>Example Sentence:</strong> {vocab.exampleSentence}</p>
              <p><strong>Review Score:</strong> {vocab.reviewScore}</p>
              <div className="card-actions">
                <button onClick={() => handleScoreChange(vocab._id, vocab.reviewScore + 1)}>👍</button>
                <button onClick={() => handleScoreChange(vocab._id, Math.max(0, vocab.reviewScore - 1))}>👎</button>
                <button onClick={() => handleUpdateWord(vocab._id)}>Update</button>
                <button onClick={() => handleDeleteWord(vocab._id)}>Delete</button>
              </div>
            </div>
          ))}
        </div>

        {/* Suggested Words Section */}
        <div className="suggested-words">
          <h2>Suggested Words</h2>
          <div className="suggested-words-list">
            {suggestedWords.map((word, index) => (
              <div key={index} className="suggested-word-card">
                <h3>{word.word}</h3>
                {/* You can customize this if you have more details from Datamuse */}
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Vocabulary;
