import React from 'react';
import '../styles/VocabularyCard.css';

const VocabularyCard = ({ vocab, onDelete, onUpdate, onIncreaseReview, onDecreaseReview }) => {
  return (
    <div className="vocabulary-card">
      <h3>{vocab.word}</h3>
      <p>Definition: {vocab.definition}</p>
      <p>Example: {vocab.exampleSentence}</p>
      <p>Review Score: {vocab.reviewScore}</p>
      <button onClick={() => onUpdate(vocab._id)}>Edit</button>
      <button onClick={() => onDelete(vocab._id)}>Delete</button>
      <button onClick={() => onIncreaseReview(vocab._id)}>Increase Review</button>
      <button onClick={() => onDecreaseReview(vocab._id)}>Decrease Review</button>
    </div>
  );
};

export default VocabularyCard;
