import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../styles/EditProfile.css';

const EditProfile = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const userId = localStorage.getItem('userId');
        if (userId) {
          const response = await axios.get(`http://localhost:3000/api/users/${userId}`);
          setUsername(response.data.username);
        }
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };
    fetchUserData();
  }, []);

  const handleSave = async () => {
    try {
      const userId = localStorage.getItem('userId');
      await axios.put(`http://localhost:3000/api/users/${userId}`, { username, password });
      alert('Profile updated successfully!');
      navigate('/vocabulary'); // Redirect to Vocabulary list after saving
    } catch (error) {
      console.error('Error updating profile:', error);
    }
  };

  const handleGoBackMainMenu = () => {
    navigate('/');
  };

  const handleGoBackMyVocabulary = () => {
    navigate('/vocabulary');
  };

  return (
    <div className="edit-profile-container">
      <h2>Edit Profile</h2>
      <form className="edit-profile-form">
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="button" onClick={handleSave}>Save Changes</button>
        <button type="button" onClick={handleGoBackMainMenu}>Go Back to Main Menu</button>
        <button type="button" onClick={handleGoBackMyVocabulary}>Go Back to My Vocabulary</button>
      </form>
    </div>
  );
};

export default EditProfile;
