import React from 'react';
import '../styles/VocabularyCard.css';

const VocabularyCard = ({ vocab, onDelete, onUpdate, onIncreaseReview, onDecreaseReview }) => {
  return (
    <div className="vocabulary-card">
      <div className="card-header">
        <h3 className="card-title">{vocab.word}</h3>
        <div className="card-buttons">
          <button className="icon-button" onClick={() => onDelete(vocab._id)}>
            <i className="fas fa-trash"></i> {/* Trash bin icon */}
          </button>
          <button className="icon-button" onClick={() => onUpdate(vocab._id)}>
            <i className="fas fa-plus"></i> {/* Plus icon */}
          </button>
        </div>
      </div>
      <p className="definition"><strong>Definition:</strong> {vocab.definition}</p>
      <p className="example"><strong>Example Sentence:</strong> {vocab.exampleSentence}</p>
      <div className="review-buttons">
        <button onClick={() => onIncreaseReview(vocab._id)}>üëç Like</button>
        <button onClick={() => onDecreaseReview(vocab._id)}>üëé Dislike</button>
      </div>
    </div>
  );
};

export default VocabularyCard;
