import React, { useState } from 'react';
import axios from 'axios';

const AddWordForm = ({ onAddWord, onCancel }) => {
  const [word, setWord] = useState('');
  const [definition, setDefinition] = useState('');
  const [exampleSentence, setExampleSentence] = useState('');

  // Function to generate definition
  const generateDefinition = async () => {
    try {
      const response = await axios.get(`YOUR_DEFINITION_API_URL/${word}`); // Replace with your API URL
      const { definition, exampleSentence } = response.data; // Adjust based on your API response
      setDefinition(definition);
      setExampleSentence(exampleSentence);
    } catch (error) {
      console.error('Error generating definition:', error);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onAddWord({ word, definition, exampleSentence });
    setWord('');
    setDefinition('');
    setExampleSentence('');
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Word:</label>
        <input
          type="text"
          value={word}
          onChange={(e) => setWord(e.target.value)}
          required
        />
      </div>
      <div>
        <label>Definition:</label>
        <textarea
          value={definition}
          onChange={(e) => setDefinition(e.target.value)}
          required
        />
      </div>
      <div>
        <label>Example Sentence:</label>
        <textarea
          value={exampleSentence}
          onChange={(e) => setExampleSentence(e.target.value)}
        />
      </div>
      <button type="button" onClick={generateDefinition}>
        Generate Definition
      </button>
      <button type="submit">Add Word</button>
      <button type="button" onClick={onCancel}>Cancel</button>
    </form>
  );
};

export default AddWordForm;
