import React from 'react';

const VocabularyCard = ({ vocab, onDelete, onUpdate, onIncreaseReview, onDecreaseReview }) => {
  return (
    <div className="vocabulary-card">
      <h3>{vocab.word}</h3>
      <p><strong>Definition:</strong> {vocab.definition}</p>
      <p><strong>Example Sentence:</strong> {vocab.exampleSentence}</p>
      <p><strong>Review Score:</strong> {vocab.reviewScore}</p>
      
      <div className="review-actions">
        <button onClick={() => onIncreaseReview(vocab._id)}>Up</button>
        <button onClick={() => onDecreaseReview(vocab._id)}>Down</button>
      </div>

      <div className="card-actions">
        <button onClick={() => onUpdate(vocab._id)}>Update</button>
        <button onClick={() => onDelete(vocab._id)}>Delete</button>
      </div>
    </div>
  );
};

export default VocabularyCard;
