import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../styles/EditProfile.css';
import { FaHome, FaBook } from 'react-icons/fa';

const API_URL = 'http://localhost:3000/api/users'; // Adjust according to your API endpoint

const EditProfile = () => {
  const navigate = useNavigate();
  const [userId, setUserId] = useState(localStorage.getItem('userId'));
  const [username, setUsername] = useState('');
  const [currentPassword, setCurrentPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await axios.get(`${API_URL}/${userId}`);
        setUsername(response.data.username);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    if (userId) {
      fetchUserData();
    }
  }, [userId]);

  const handleUpdate = async (e) => {
    e.preventDefault();

    if (!username) {
      setErrorMessage('Username cannot be empty');
      return;
    }

    if (newPassword !== confirmPassword) {
      setErrorMessage('New passwords do not match');
      return;
    }

    const updateData = { username };

    if (newPassword) {
      updateData.password = newPassword; // Add new password if provided
    }

    try {
      await axios.put(`${API_URL}/${userId}`, updateData);
      navigate('/vocabulary'); // Navigate back to the vocabulary page
    } catch (error) {
      setErrorMessage('Error updating profile: ' + error.message);
    }
  };

  return (
    <div className="edit-profile-container">
      <h2>Edit Profile</h2>
      {errorMessage && <p className="error-message">{errorMessage}</p>}
      <form onSubmit={handleUpdate}>
        <div className="form-group">
          <label>Username</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>Current Password</label>
          <input
            type="password"
            value={currentPassword}
            onChange={(e) => setCurrentPassword(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>New Password</label>
          <input
            type="password"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>Confirm New Password</label>
          <input
            type="password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
          />
        </div>
        <button type="submit">Update Profile</button>
      </form>
      <div className="navigation-buttons">
        <button onClick={() => navigate('/vocabulary')}>
          <FaBook /> Return to My Vocabulary
        </button>
        <button onClick={() => navigate('/')}>
          <FaHome /> Return Home
        </button>
      </div>
    </div>
  );
};

export default EditProfile;
