import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../styles/Vocabulary.css';

const API_URL = 'http://localhost:3000/api/vocab';

const Vocabulary = () => {
  const [vocabularyList, setVocabularyList] = useState([]);
  const [userId, setUserId] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchVocabulary = async () => {
      try {
        const userIdFromStorage = localStorage.getItem('userId');
        if (userIdFromStorage) {
          setUserId(userIdFromStorage);
          const response = await axios.get(`${API_URL}?userId=${userIdFromStorage}`);
          setVocabularyList(response.data);
        }
      } catch (error) {
        console.error('Error fetching vocabulary:', error);
      }
    };

    fetchVocabulary();
  }, []);

  const handleAddWord = () => {
    navigate('/add-word');
  };

  const handleDeleteWord = () => {
    navigate('/delete-word');
  };

  const handleUpdateWord = () => {
    navigate('/update-word');
  };

  const handleLogout = () => {
    localStorage.removeItem('userId'); // Remove user ID from localStorage
    navigate('/'); // Navigate to the sign-in page
  };

  return (
    <div className="vocabulary-container">
      <div className="sidebar">
        <h3>Menu</h3>
        <button onClick={handleAddWord}>Add Word</button>
        <button onClick={handleDeleteWord}>Delete Word</button>
        <button onClick={handleUpdateWord}>Update Word</button>
        <button onClick={handleLogout}>Logout</button>
      </div>
      <div className="vocabulary-content">
        <h2>Your Vocabulary</h2>
        <div className="vocabulary-cards">
          {vocabularyList.map((vocab) => (
            <div key={vocab._id} className="vocabulary-card">
              <h3>{vocab.word}</h3>
              <p><strong>Definition:</strong> {vocab.definition}</p>
              <p><strong>Example Sentence:</strong> {vocab.exampleSentence}</p>
              <p><strong>Review Score:</strong> {vocab.reviewScore}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Vocabulary;
