import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_URL = 'http://localhost:3000/api/vocab';

const UpdateWordForm = ({ wordId, onUpdate, onCancel }) => {
  const [wordData, setWordData] = useState({ word: '', definition: '', exampleSentence: '' });

  useEffect(() => {
    const fetchWord = async () => {
      try {
        const response = await axios.get(`${API_URL}/${wordId}`);
        setWordData(response.data);
      } catch (error) {
        console.error('Error fetching word data:', error);
      }
    };
    fetchWord();
  }, [wordId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setWordData({ ...wordData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await onUpdate(wordId, wordData);
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        name="word"
        value={wordData.word}
        onChange={handleChange}
        placeholder="Word"
        required
      />
      <input
        type="text"
        name="definition"
        value={wordData.definition}
        onChange={handleChange}
        placeholder="Definition"
        required
      />
      <textarea
        name="exampleSentence"
        value={wordData.exampleSentence}
        onChange={handleChange}
        placeholder="Example Sentence"
        required
      />
      <button type="submit">Update Word</button>
      <button type="button" onClick={onCancel}>Cancel</button>
    </form>
  );
};

export default UpdateWordForm;
