import React, { useState } from 'react';

const UpdateWordForm = ({ currentWord, onUpdate, onCancel }) => {
  const [word, setWord] = useState(currentWord.word);
  const [definition, setDefinition] = useState(currentWord.definition);

  const handleSubmit = (e) => {
    e.preventDefault();
    const updatedWord = { ...currentWord, word, definition }; // Update the fields you want to change
    onUpdate(currentWord._id, updatedWord); // Call the update function
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Word:
        <input
          type="text"
          value={word}
          onChange={(e) => setWord(e.target.value)}
          required
        />
      </label>
      <label>
        Definition:
        <textarea
          value={definition}
          onChange={(e) => setDefinition(e.target.value)}
          required
        />
      </label>
      <button type="submit">Update Word</button>
      <button type="button" onClick={onCancel}>Cancel</button>
    </form>
  );
};

export default UpdateWordForm;
