import React, { useEffect, useState } from 'react';
import axios from 'axios';
import VocabularyContainer from '../components/VocabularyContainer';
import '../styles/Vocabulary.css';
import UpdateWordForm from '../components/UpdateWordForm';

const API_URL = 'http://localhost:3000/api/vocab';

const Vocabulary = () => {
  const [vocabularyList, setVocabularyList] = useState([]);
  const [suggestedWords, setSuggestedWords] = useState([]);
  const [editWord, setEditWord] = useState(null); // Add this line for editWord state

  useEffect(() => {
    fetchVocabulary();
    fetchSuggestedWords();
  }, []);

  const fetchVocabulary = async () => {
    try {
      const userId = localStorage.getItem('userId');
      if (userId) {
        const response = await axios.get(`${API_URL}?userId=${userId}`);
        setVocabularyList(response.data);
      }
    } catch (error) {
      console.error('Error fetching vocabulary:', error);
    }
  };

  const fetchSuggestedWords = async () => {
    const userId = localStorage.getItem('userId');
    if (!userId) return;
    try {
      const response = await axios.get(`${API_URL}/suggestions/${userId}`);
      setSuggestedWords(response.data);
    } catch (error) {
      console.error('Error fetching suggested words:', error);
    }
  };

  const handleAddWord = async (newWord) => {
    try {
      const response = await axios.post(API_URL, newWord);
      setVocabularyList((prevList) => [...prevList, response.data]);
    } catch (error) {
      console.error('Error adding new word:', error);
    }
  };

  const handleAddSuggestedWord = async (suggestedWord) => {
    try {
      const response = await axios.post(API_URL, suggestedWord);
      setVocabularyList((prevList) => [...prevList, response.data]);
      setSuggestedWords((prevList) =>
        prevList.filter((word) => word.word !== suggestedWord.word)
      );
    } catch (error) {
      console.error('Error adding suggested word:', error);
    }
  };

  const handleDeleteWord = async (wordId) => {
    try {
      await axios.delete(`${API_URL}/${wordId}`);
      setVocabularyList((prevList) => prevList.filter((word) => word._id !== wordId));
    } catch (error) {
      console.error('Error deleting word:', error);
    }
  };

  const handleUpdateWord = async (wordId, updatedWord) => {
    try {
      await axios.put(`${API_URL}/${wordId}`, updatedWord);
      setVocabularyList((prevList) =>
        prevList.map((word) => (word._id === wordId ? updatedWord : word))
      );
      setEditWord(null); // Reset editWord after updating
    } catch (error) {
      console.error('Error updating word:', error);
    }
  };

  const handleEditClick = (word) => {
    setEditWord(word); // Set the word to be edited
  };

  const handleCancelEdit = () => {
    setEditWord(null); // Reset edit mode
  };

  const handleEditProfile = () => {
    window.location.href = '/edit-profile';
  };

  const handleLogout = () => {
    localStorage.removeItem('userId');
    window.location.href = '/';
  };

  return (
    <div className="vocabulary-page">
      {editWord ? ( // Conditional rendering based on editWord state
        <UpdateWordForm
          currentWord={editWord}
          onUpdate={handleUpdateWord}
          onCancel={handleCancelEdit}
        />
      ) : (
        <VocabularyContainer
          vocabularyList={vocabularyList}
          suggestedWords={suggestedWords}
          onAddWord={handleAddWord}
          onAddSuggestedWord={handleAddSuggestedWord}
          onDeleteWord={handleDeleteWord}
          onUpdateWord={handleUpdateWord}
          onEditProfile={handleEditProfile}
          onLogout={handleLogout}
          onEditClick={handleEditClick} // Pass edit handler to VocabularyContainer
        />
      )}
    </div>
  );
};

export default Vocabulary;
