// src/pages/VocabularyPage.js
import React, { useEffect, useState } from 'react';
import { getWords, addWord, updateWord, deleteWord } from '../services/vocabService';
import WordCard from '../components/WordCard';

const VocabularyPage = () => {
  const [words, setWords] = useState([]);
  const userId = "user_id_here"; // Replace with actual user ID

  useEffect(() => {
    // Fetch words on component load
    getWords(userId).then(setWords);
  }, [userId]);

  const handleDelete = async (id) => {
    await deleteWord(id);
    setWords(words.filter(word => word._id !== id));
  };

  const handleEdit = async (updatedWord) => {
    const word = await updateWord(updatedWord._id, updatedWord);
    setWords(words.map(w => (w._id === word._id ? word : w)));
  };

  return (
    <div>
      <h2>My Vocabulary</h2>
      <div>
        {words.map(word => (
          <WordCard key={word._id} word={word} onDelete={handleDelete} onEdit={handleEdit} />
        ))}
      </div>
    </div>
  );
};

export default VocabularyPage;
