import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../styles/EditProfile.css';

const EditProfile = () => {
  const [username, setUsername] = useState('');
  const [currentPassword, setCurrentPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmNewPassword, setConfirmNewPassword] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserData = async () => {
      const userId = localStorage.getItem('userId');
      try {
        const response = await axios.get(`http://localhost:3000/api/users/${userId}`);
        setUsername(response.data.username);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };
    fetchUserData();
  }, []);

  const goBackHome = () => {
    navigate('/');
  };

  const goBackToVocabulary = () => {
    navigate('/vocabulary');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!username.trim()) {
      alert('Username cannot be empty.');
      return;
    }

    if (newPassword && newPassword !== confirmNewPassword) {
      alert('New passwords do not match.');
      return;
    }

    try {
      const userId = localStorage.getItem('userId');
      await axios.put(`http://localhost:3000/api/users/${userId}`, {
        username,
        currentPassword,
        newPassword: newPassword || undefined, // Only update if newPassword is provided
      });
      alert('Profile updated successfully.');
      navigate('/vocabulary'); // Automatically redirect to vocabulary page after update
    } catch (error) {
      console.error('Error updating profile:', error);
      alert('Failed to update profile.');
    }
  };

  return (
    <div className="edit-profile-container">
      <h2>Edit Profile</h2>

      <form onSubmit={handleSubmit}>
        <div>
          <label>Username</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Current Password</label>
          <input
            type="password"
            value={currentPassword}
            onChange={(e) => setCurrentPassword(e.target.value)}
            required
          />
        </div>
        <div>
          <label>New Password</label>
          <input
            type="password"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
          />
        </div>
        <div>
          <label>Confirm New Password</label>
          <input
            type="password"
            value={confirmNewPassword}
            onChange={(e) => setConfirmNewPassword(e.target.value)}
          />
        </div>
        <button type="submit">Update Profile</button>
      </form>

      <div className="button-container">
        <button className="return-home" onClick={goBackHome}>
          <i className="fas fa-home"></i> Return Home
        </button>
        <button className="return-vocabulary" onClick={goBackToVocabulary}>
          <i className="fas fa-book"></i> Return to My Vocabulary
        </button>
      </div>
    </div>
  );
};

export default EditProfile;
