import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../styles/EditProfile.css';

const EditProfile = () => {
    const [username, setUsername] = useState('');
    const [currentPassword, setCurrentPassword] = useState('');
    const [newPassword, setNewPassword] = useState('');
    const [confirmNewPassword, setConfirmNewPassword] = useState('');
    const [feedback, setFeedback] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (newPassword !== confirmNewPassword) {
            setFeedback('New passwords do not match');
            return;
        }

        try {
            const userId = localStorage.getItem('userId');
            const response = await axios.put(`http://localhost:3000/api/users/${userId}`, {
                username,
                password: newPassword,
            });

            if (response.status === 200) {
                setFeedback(response.data.message);
                setTimeout(() => navigate('/vocabulary'), 1500);
            }
        } catch (error) {
            setFeedback('Error updating profile');
            console.error(error);
        }
    };

    return (
        <div className="edit-profile-container">
            <h2>Edit Profile</h2>
            <form className="edit-profile-form" onSubmit={handleSubmit}>
                <label className="edit-label">Username</label>
                <input
                    type="text"
                    className="edit-input"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    placeholder="Enter new username"
                    required
                />

                <label className="edit-label">Current Password</label>
                <input
                    type="password"
                    className="edit-input"
                    value={currentPassword}
                    onChange={(e) => setCurrentPassword(e.target.value)}
                    placeholder="Enter current password"
                />

                <label className="edit-label">New Password</label>
                <input
                    type="password"
                    className="edit-input"
                    value={newPassword}
                    onChange={(e) => setNewPassword(e.target.value)}
                    placeholder="Enter new password"
                />

                <label className="edit-label">Confirm New Password</label>
                <input
                    type="password"
                    className="edit-input"
                    value={confirmNewPassword}
                    onChange={(e) => setConfirmNewPassword(e.target.value)}
                    placeholder="Confirm new password"
                />

                <button type="submit" className="edit-submit-button">Update Profile</button>
            </form>
            {feedback && <p className="edit-feedback">{feedback}</p>}

            <button className="edit-back-button" onClick={() => navigate('/')}>Return Home</button>
            <button className="edit-back-button" onClick={() => navigate('/my-vocabulary')}>Return to My Vocabulary</button>
        </div>
    );
};

export default EditProfile;
